
#include <stdio.h>
#include <fcntl.h>
#include <assert.h>
#include <string.h>
#include <stdlib.h>
#include "get_next_line.h"

void	print_line(char *line)
{
	// printf("----%s----\n", __func__);
	printf("%s|", line);
	printf("\n-------------------\n");
}

void	checker(char *line, char *expected)
{
	// assert(strcmp(line, expected) == 0);
	print_line(line);
	free(line);
}

void one_char(void)
{
	printf("----%s----\n", __func__);
	char *line;
	int fd = open("./files/1char.txt", O_RDONLY);
	if (fd != -1)
	{
		line = get_next_line(fd);
		checker(line, "0");
		close(fd);
	}
}

void empty_file(void)
{
	printf("----%s----\n", __func__);
	char *line;
	int fd = open("./files/empty.txt", O_RDONLY);
	if (fd != -1)
	{
		line = get_next_line(fd);
		checker(line, NULL);
		close(fd);
	}
}

void giant_line(void)
{
	printf("----%s----\n", __func__);
	char *line;
	int fd = open("./files/giant_line.txt", O_RDONLY);
	if (fd != -1)
	{
		line = get_next_line(fd);
		checker(line, "");
		close(fd);
	}
}

void giant_line_nl(void)
{
	printf("----%s----\n", __func__);
	char *line;
	int fd = open("./files/giant_line_nl.txt", O_RDONLY);
	if (fd != -1)
	{
		line = get_next_line(fd);
		checker(line, "");
		line = get_next_line(fd);
		checker(line, "another line!!!");
		close(fd);
	}
}

void lines_around_10(void)
{
	printf("----%s----\n", __func__);
	char *line;
	int fd = open("./files/lines_around_10.txt", O_RDONLY);
	if (fd != -1)
	{
		line = get_next_line(fd);
		checker(line, "0123456789\n");
		line = get_next_line(fd);
		checker(line, "012345678\n");
		line = get_next_line(fd);
		checker(line, "90123456789\n");
		line = get_next_line(fd);
		checker(line, "0123456789\n");
		line = get_next_line(fd);
		checker(line, "xxxx");
		close(fd);
	}
}

void multiple_nl(void)
{
	printf("----%s----\n", __func__);
	char *line;
	int fd = open("./files/multiple_nl.txt", O_RDONLY);
	if (fd != -1)
	{
		while ((line = get_next_line(fd)) != NULL)
		{
			checker(line, "\n");
		}
		close(fd);
	}
}

void one_line_no_nl(void)
{
	printf("----%s----\n", __func__);
	char *line;
	int fd = open("./files/one_line_no_nl.txt", O_RDONLY);
	if (fd != -1)
	{
		line = get_next_line(fd);
		checker(line, "abcdefghijklmnopqrstuvwxyz");
		close(fd);
	}
}

void only_nl(void)
{
	printf("----%s----\n", __func__);
	char *line;
	int fd = open("./files/only_nl.txt", O_RDONLY);
	if (fd != -1)
	{
		line = get_next_line(fd);
		checker(line, "\n");
		close(fd);
	}
}

void multiple_lines(void)
{
	printf("----%s----\n", __func__);
	char *line;
	int fd = open("./files/multiple_lines.txt", O_RDONLY);
	if (fd != -1)
	{
		line = get_next_line(fd);
		checker(line, "aaaaaaaaaa");
		line = get_next_line(fd);
		checker(line, "bbbbbbbbbb");
		line = get_next_line(fd);
		checker(line, "cccccccccc");
		line = get_next_line(fd);
		checker(line, "dddddddddd");
		close(fd);
	}
}

int main(int ac, char **av)
{
	(void)ac;
	int test = atoi(av[1]);
	switch (test)
	{
	case 1:
		one_char();
		break;
	case 2:
		empty_file();
		break;
	case 3:
		giant_line();
		break;
	case 4:
		giant_line_nl();
		break;
	case 5:
		lines_around_10();
		break;
	case 6:
		multiple_nl();
		break;
	case 7:
		one_line_no_nl();
		break;
	case 8:
		only_nl();
		break;
	case 9:
		multiple_lines();
		break;
	default:
		printf("Test %d not implemented\n", test);
		break;
	}
	return (0);
}